def note(note,previousTime, attackTime,releaseTime,fr):
    deltaTime = previousTime + attackTime
    #print("initial",deltaTime)
    cmds.rotate(0, 0, 0,"note"+ str(note))
    cmds.setKeyframe("note" + str(note), t=previousTime*fr, at='rotateX')
    cmds.setKeyframe("note"+str(note), t=deltaTime*fr, at='rotateX')

    cmds.rotate('4deg', 0, 0,"note"+ str(note))
    cmds.setKeyframe("note"+str(note), t=(deltaTime + 0.01)*fr, at='rotateX')
    #print("before", deltaTime)
    deltaTime = deltaTime + releaseTime
    #print("after",deltaTime)
    cmds.setKeyframe("note" + str(note), t=(deltaTime - 0.01 )* fr, at='rotateX')

    cmds.rotate(0, 0, 0, "note" + str(note))
    cmds.setKeyframe("note" + str(note), t=(deltaTime) * fr, at='rotateX')
    return deltaTime












